import './index.css'
const books = [
{
  title : "I love you to the moon and back",
  author : "pratyay gaurav",
  img :
   "https://m.media-amazon.com/images/I/51p2SDOCV9L._SX482_BO1,204,203,200_.jpg"
},

 {
  title: "Rich Dad",
  author: "qwerty",
  img: "https://m.media-amazon.com/images/I/51p2SDOCV9L._SX482_BO1,204,203,200_.jpg",
},

 {
  title: "mythology",
  author: "Krishna",
  img: "https://m.media-amazon.com/images/I/51p2SDOCV9L._SX482_BO1,204,203,200_.jpg",
}
]

function BookList() {
  return (
    <section className="booklist">
      {books.map((book)=>
      {
        const {img,title,author}=book;
        return (
          <Book book={book}></Book>
        )




      })}
    </section>
  );
}

